document.addEventListener("DOMContentLoaded",function(){initNavigation();initCalendar();initCharts();initSearchFunctionality();initEventCards()});function initNavigation(){const e=document.querySelectorAll(".nav-link"),t=window.location.pathname.split("/").pop();e.forEach(e=>{e.getAttribute("href")===t&&e.classList.add("active")})}function initCalendar(){const e=document.querySelector(".calendar-container");if(e){const t=new Date,n=t.getMonth(),a=t.getFullYear(),i=document.querySelector(".calendar-title"),r=document.querySelector(".calendar-days");if(i&&r){updateCalendarTitle(i,n,a);const e=new Date(a,n,1),o=new Date(a,n+1,0).getDate(),l=e.getDay(),c=[];for(let e=0;e<l;e++)c.push(createCalendarDay("",!0));for(let e=1;e<=o;e++){const i=new Date(a,n,e),r=i.getTime()===new Date(t.getFullYear(),t.getMonth(),t.getDate()).getTime(),o=Math.random()>.7;c.push(createCalendarDay(e,!1,r,o))}r.innerHTML="",c.forEach(e=>{r.appendChild(e)})}const o=document.querySelector(".calendar-nav-prev"),l=document.querySelector(".calendar-nav-next"),c=document.querySelector(".calendar-today-btn");o&&o.addEventListener("click",()=>{changeMonth(-1)}),l&&l.addEventListener("click",()=>{changeMonth(1)}),c&&c.addEventListener("click",()=>{goToToday()})}}function updateCalendarTitle(e,t,n){const a=["January","February","March","April","May","June","July","August","September","October","November","December"];e.textContent=`${a[t]} ${n}`}function createCalendarDay(e,t,n=!1,a=!1){const i=document.createElement("div");if(i.className=`calendar-day${t?" other-month":""}${n?" today":""}${a?" has-events":""}`,e){i.innerHTML=`<div class="calendar-day-number">${e}</div>`;if(a){const e=Math.floor(3*Math.random())+1;for(let t=0;t<e;t++){const e=document.createElement("div");e.className="calendar-event",e.textContent=getRandomEventTitle(),e.addEventListener("click",()=>{window.location.href="event_details.html"}),i.appendChild(e)}}}return i}function getRandomEventTitle(){const e=["AI Workshop","Tech Meetup","Startup Pitch","Coding Bootcamp","Design Thinking","Product Launch","Networking Event","Hackathon"];return e[Math.floor(Math.random()*e.length)]}function changeMonth(e){}function goToToday(){}function initCharts(){if("undefined"!=typeof Chart){const e=document.getElementById("categoryChart");if(e){new Chart(e,{type:"doughnut",data:{labels:["AI/ML","Web Development","Mobile","Blockchain","IoT","Cloud","Other"],datasets:[{data:[45,25,15,8,5,2],backgroundColor:["#4285f4","#34a853","#ea4335","#fbbc05","#5f6368","#185abc","#d93025"],borderWidth:1}]},options:{responsive:!0,plugins:{legend:{position:"right"}}}})}}const e=document.getElementById("trendChart");if(e){new Chart(e,{type:"line",data:{labels:["Jan","Feb","Mar","Apr","May","Jun"],datasets:[{label:"AI/ML Events",data:[12,19,15,17,22,24],borderColor:"#4285f4",tension:.1},{label:"Web3 Events",data:[5,12,18,15,8,10],borderColor:"#34a853",tension:.1}]},options:{responsive:!0}})}}function initSearchFunctionality(){const e=document.querySelector(".search-box form");e&&e.addEventListener("submit",t=>{t.preventDefault();const n=e.querySelector("input").value;console.log("Searching for:",n)})}function initEventCards(){const e=document.querySelectorAll(".event-card");e.length>0&&e.forEach(e=>{e.addEventListener("click",()=>{window.location.href="event_details.html"})})}
